const ButtonWrapper = styled.div`
  background-color: ${p => p.theme.color.toggleWrapper};
  width: calc(3px + 60px + 60px + 3px);
  height: calc(3px + 60px + 3px);
  margin-left: auto;
  margin-right: auto;
  border-radius: ${p => p.theme.borderRadius};
  display: flex;
  position: relative;
  &::after {
    content: "";
    position: absolute;
    height: 60px;
    width: 60px;
    border-radius: ${p => p.theme.borderRadius};
    margin-top: 3px;
    margin-left: 3px;
    background-color: ${p => p.theme.color.box};
    z-index: 0;
    transform: ${p => !p.left && 'translateX(100%)'};
    transition: transform .2s ease-out;
  }
`

const StyledButton = styled.button`
  height: 60px;
  width: 60px;
  border-radius: ${p => p.theme.borderRadius};
  outline: none;
  border: 0;
  margin-top: 3px;
  margin-left: 3px;
  background-color: transparent;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1;
  cursor: pointer;
`

const Icon = styled.svg`
  fill: ${p => p.active ? p.theme.color.activeTheme : p.theme.color.text};
`